document.addEventListener('DOMContentLoaded', () => {
    try {
        document.querySelector('.menu__link').classList.add('-active');
    } catch (e) {}
    const clearAnchors = (anchors => anchors.forEach(anchor => anchor.classList.remove('-active')));
    document.querySelectorAll('a[href^="#"]').forEach((anchor, i, anchors) => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            clearAnchors(anchors)
            e.target.classList.add('-active');
            document.querySelector(this.getAttribute('href')).scrollIntoView({
                behavior: 'smooth'
            });
        });
    });
});